import { EditIcon } from "@chakra-ui/icons";
import { Box, Button, Flex, FormControl, FormLabel, Input, Modal, ModalBody, ModalCloseButton, ModalContent, ModalFooter, ModalHeader, ModalOverlay, Textarea, useDisclosure } from "@chakra-ui/react";
import styled from "@emotion/styled/types/base";
import { memo, useRef, useState, VFC } from "react";
import { Rating } from 'react-simple-star-rating'

type  Props = {
    id: number
}

const ReviewModal: VFC<Props> = memo((props) => {
    const { id } = props
    const { isOpen, onOpen, onClose } = useDisclosure()
    const initialRef = useRef<HTMLInputElement>(null)
    //レビュー初期値
    const [ rating, setRating ] = useState(0)
    const handleRating = (rate: number) => {
        setRating(rate)
    }

const Article = styled.article`
  width: 250px;
  padding: 16px;
  text-align: center;
  border: 1px solid #eee;
  box-shadow: 0 2px 3px #ccc;
  margin: 10px;
  box-sizing: border-box;
  cursor: pointer;
  :hover,
  :active {
    background-color: #c0ddf5;
  }
  .Author {
    margin: 16px 0;
    color: #ccc;
  }
`;

    return (
        <>
            <Button pl={0} size='xs' leftIcon={<EditIcon />} variant='none' onClick={onOpen}>レビューを書く</Button>

            <Modal
                initialFocusRef={initialRef}
                isOpen={isOpen}
                onClose={onClose}
            >
                <ModalOverlay />
                <ModalContent>
                <ModalHeader>アニメレビュー</ModalHeader>
                <ModalCloseButton />
                <ModalBody pb={6}>
                    <FormControl>
                        <FormLabel>レビュー評価</FormLabel>
                        <Box display='flex'>
                            <Rating fullStyle={{display: 'inline-block'}} onClick={handleRating} ratingValue={rating} />
                        </Box>
                    </FormControl>

                    <FormControl mt={4}>
                        <FormLabel>レビューコメント</FormLabel>
                        <Textarea />
                    </FormControl>
                </ModalBody>

                <ModalFooter>
                    <Button colorScheme='blue' mr={3}>
                    Save
                    </Button>
                    <Button onClick={onClose}>Cancel</Button>
                </ModalFooter>
                </ModalContent>
            </Modal>
        </>
    )
})

export default ReviewModal
