import { Box, Button, Divider, Flex, FormControl, FormLabel, Heading, Input, Text, VStack } from "@chakra-ui/react"
import { BeatLoader } from '@chakra-ui/icons'
import { useForm } from "react-hook-form"

type FormInputs = {
    email: string;
    password: string;
  };

const SigninForm = () => {
    const { register, formState: { errors }, handleSubmit, formState } = useForm<FormInputs>({
        mode: 'all',
        defaultValues: {
            email: '',
            password: '',
          }
    })
    return (
        <>
            {/* <Header /> */}
            {/* <Box> */}
                <Flex justify='center' align='center' h='100vh'>
                    <Box bg='white' w='sm' p={4} borderRadius='10px' shadow='md'>
                        <Heading as='h1' size='lg' textAlign='center'>ログイン</Heading>
                        <Divider my={4}/>
                        <form>
                            <VStack spacing={5}>
                                <FormControl>
                                    <FormLabel id="email">メールアドレス</FormLabel>
                                    <Input type="email" name='email' placeholder="test@example.com"/>
                                </FormControl>
                                <FormControl>
                                    <FormLabel id="password">パスワード</FormLabel>
                                    <Input type="text" name='password'/>
                                </FormControl>
                                <Button w='100%' color='white' bg='blue.400' _hover={{opacity: 0.8}} isLoading spinner={<BeatLoader size={8} color="white" />}>ログイン</Button>
                            </VStack>
                        </form>

                    </Box>
                </Flex>  
            {/* </Box> */}

        </>
    )
}

export default SigninForm
