import { useCallback, useEffect, useState } from 'react'
import { useRecoilState, useRecoilValue } from 'recoil'

import { client } from "../lib/api/client"
import { ApiKeyValue } from '../store/ApiKeyValue'
import { useMessage } from './message/useMessage'
import { AnnictApiType } from '../types/api/AnnictApiType'
import { AnimeLists } from '../store/AnimeLists'

type AnimeApiSearch = () => {
        animeLists: AnnictApiType[];
        getAnimes: (page: number) => void;
        hasMore: boolean;
        isFetching: boolean;
}

export const useAnimeApiSearch: AnimeApiSearch = () => {
    const [ isFetching, setIsFetching]  = useState(false);
    const [ hasMore, setHasMore ] = useState(true)
    const [ apiKeyValue, setApiKeyValue ] = useRecoilState(ApiKeyValue)
    const [ animeLists, setAnimeLists ] = useRecoilState(AnimeLists)
    const { showMessage } = useMessage()

        //初回表示リスト取得
        const getAnimes = useCallback(async(page: number = 1) => {
            try {
                // setIsFetching(true);
                // console.log(page);

                const filter_season = `${apiKeyValue.year}-${apiKeyValue.cour}`
                const getanime = await client.get('/api/annicts/search', {
                    params: {
                        filter_season,
                        page
                    }
                })

                const animeDate: AnnictApiType[] = getanime.data.works
                // if (animeDate.length < 1) {
                //     setHasMore(false);
                //     return;
                // }
                // setAnimeLists(animeDate)
                setAnimeLists(animeDate)

            } catch (error) {
                showMessage({title: 'アニメ一覧の取得に失敗しました', status: "error"})
            } 
            // finally {
            //     setIsFetching(false);
            // }
        }, [animeLists, apiKeyValue])

        // 初回レンダリング時、検索時最初のページ取得
        useEffect(() => {
            getAnimes()        
        }, [apiKeyValue])

    return { animeLists, hasMore, isFetching, getAnimes }
}
