import { useState } from "react";
    type Size = () => {
        partsSize: string | null;
    }
    export const useGetSize: Size  = () => {
        const [ partsSize, setPartsSize ] = useState<string | null(null);
        //画面幅に応じたモーダルサイズ設定
        const innerWidth = window.innerWidth　< 768 ? 'sm' : 'md'
        setPartsSize(innerWidth)

        return { partsSize }
    }
