import { Box, Container, Divider, Image, Text } from "@chakra-ui/react";
import { VFC } from "react";
import { useLocation } from "react-router-dom";
import ReactStars from "react-stars";
import { AnnictApiType } from "types/api/AnnictApiType";

const ReviewPage: VFC = () => {
    const location = useLocation<string>()
    const images = location.state
    // // const { images } = state
    console.log(images);

    const image = images ? images : 'https://lab.esprlog.net/annict/img/no_image.png'

    return (
        <Box w={{base: '80%', md: '60%'}} h='100vh' bg='#ffff' m='100px auto 20px auto' borderRadius='10px'>
            <Box w='100%'>
                <Image src={image}  borderRadius='10px 10px 0 0' w='100%' objectFit='cover'/>
            </Box>
            <Box p={3}>
                <Text as='h2' mb={3} fontWeight='bold'>レビュー</Text>
                <Divider size={'lg'}/>
                <Box>
                    <Text as='p' fontWeight='bold'>タイトル</Text>
                    <ReactStars edit={false}/>
                </Box>
            </Box>
        </Box>
    )
}

export default ReviewPage
