import { useEffect } from 'react'
import axios from "axios"
import { client } from "../lib/api/client"
import { useCourFactory } from './useCourFactory'
import { useYearFactory } from './useYearFactory'
import { useRecoilState, useRecoilValue } from 'recoil'
import { Year } from '../store/Year'
import { Cour } from '../store/Cour'
import { ApiKeyValue } from '../store/ApiKeyValue'
import { useMessage } from './message/useMessage'

export const useAnimeApiSearch = () => {
    const selectYear = useRecoilValue(Year)
    const selectCour = useRecoilValue(Cour)
    const [ apiKeyValue, setApiKeyValue ] = useRecoilState(ApiKeyValue)
    const { showMessage } = useMessage()
    // const { current_season } = useCourFactory()
    const testData = 'Hello'
    useEffect(() => {

        const getAnimes = async(year: string, cour: string) => {
            try {
                const getanime = await client.get('api/annicts/search', {
                    params: {
                        year: apiKeyValue.year,
                        cour: apiKeyValue.cour
                    }
                })
                console.log(getanime || 'なし');
                console.log(apiKeyValue);

            } catch (error) {
                showMessage({title: 'アニメ一覧の取得に失敗しました', status: "error"})
            }
         }
         getAnimes()

        // return () => {
        // }    
    }, [apiKeyValue])    


    return { testData }
}
