import { Box, Button, Center, Image, Text, VStack } from "@chakra-ui/react";
import ReviewList from "components/organisms/ReviewList";
import { memo, useState, VFC, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { testData } from "./testData";

const ReviewPage: VFC = memo(() => {
    const [ loadIndex, setLoadIndex ] = useState(4)
    const [ isEmpty, setIsEmpty ] = useState(false);
    const location = useLocation<string>()
    const images = location.state

    const image = images ? images : 'https://lab.esprlog.net/annict/img/no_image.png'

    const displayMore = () => {
        if(loadIndex < testData.length) {
            setIsEmpty(true);
        } else {
            setLoadIndex(loadIndex + 4);
        }

    }

    useEffect(() => {
        const checkLodaList = () => {
            loadIndex > testData.length && setIsEmpty(true)
        }
        checkLodaList()
    }, [loadIndex])

    return (
        <Box w={{base: '80%', md: '60%'}} h='100%' bg='#ffff' m='100px auto 20px auto' borderRadius='10px'>
            <Box w='100%'>
                <Image src={image}  borderRadius='10px 10px 0 0' w='100%' objectFit='cover'/>
            </Box>

            <Box p={3}>
                <Text as='h2' fontWeight='bold'>レビュー一覧</Text>
                <VStack spacing={5} align='start'>
                    {testData ? (
                        testData.slice(0, loadIndex).map((val, key) => (
                            <ReviewList key={key} review={val}/>
                        ))
                        ) : null }
                </VStack>
                <Center mt={10}>
                    <Button
                    colorScheme='teal'
                    disabled={isEmpty}
                    onClick={displayMore}
                    variant="solid"
                    >
                        さらに表示
                    </Button>
                </Center>
            </Box>
        </Box>
    )
})

export default ReviewPage
