import { EditIcon } from "@chakra-ui/icons";
import { Box, Button, Text, FormControl, FormLabel, Input, Modal, ModalBody, ModalCloseButton, ModalContent, ModalFooter, ModalHeader, ModalOverlay, Textarea, useDisclosure, VStack } from "@chakra-ui/react";
import { memo, useRef, useState, VFC, useEffect } from "react";
import ReactStars from 'react-stars'

type  Props = {
    id: number
}

const ReviewModal: VFC<Props> = memo((props) => {
    const { id } = props
    const { isOpen, onOpen, onClose } = useDisclosure()
    const initialRef = useRef<HTMLInputElement>(null)

    //レビュー初期値
    const [ rating, setRating ] = useState(0)
    const getRating = (rate: number) => {
        console.log(rate);

        setRating(rate)       
    }

    //モーダルを閉じた場合、評価を初期化
    useEffect(() => {

        return () => {
            setRating(0)
        }
    }, [isOpen])

    return (
        <>
            <Button pl={0} size='xs' leftIcon={<EditIcon />} variant='none' onClick={onOpen}>レビューを書く</Button>

            <Modal
                initialFocusRef={initialRef}
                isOpen={isOpen}
                onClose={onClose}
            >
                <ModalOverlay />
                <ModalContent>
                <ModalHeader>アニメレビュー</ModalHeader>
                <ModalCloseButton />
                <ModalBody pb={6}>
                    <Box>
                        <VStack align='left'>
                            <Text>レビュー評価</Text>
                            <ReactStars value={rating} size={30} half={false} onChange={getRating}/>
                        </VStack>
                    </Box>
                    {/* <FormControl>
                        <FormLabel>レビュー評価</FormLabel>
                    </FormControl> */}
                    <form>
                        <FormControl mt={4}>
                            <FormLabel>レビューコメント</FormLabel>
                            <Textarea />
                        </FormControl>
                    </form>
                </ModalBody>

                <ModalFooter>
                    <Button colorScheme='blue' mr={3}>
                        Save
                    </Button>
                    <Button onClick={onClose}>Cancel</Button>
                </ModalFooter>
                </ModalContent>
            </Modal>
        </>
    )
})

export default ReviewModal
