import { memo, useState, useCallback, useRef } from "react"

import { Box, Center, Flex, Text, Wrap, WrapItem } from "@chakra-ui/react"
import { VFC } from "react"
import { useRecoilState, useRecoilValue } from "recoil"
import InfiniteScroll  from "react-infinite-scroller"

import { useCourFactory } from "../../customHooks/useCourFactory"
import { Year } from "../../store/Year"
import { AnnictApiType } from "../../types/api/AnnictApiType"
import AnimeCard from "../molecules/animeCard/AnimeCard"
import { useAnimeApiSearch } from "customHooks/useAnimeApiSearch"
import { client } from "lib/api/client"
import { ApiKeyValue } from "store/ApiKeyValue"
import { AnimeLists } from "store/AnimeLists"
import { BeatLoader } from "react-spinners"
import { HasMore } from "store/loding/HasMore"
import { useMessage } from "customHooks/message/useMessage"
import { useNextAnimes } from "customHooks/useNextAnimes"
import { LoadPage } from "store/LoadPage"

type Props = {
    animeLists: AnnictApiType[];
}

const AnimeCardList: VFC<Props> = memo((props) => {
    // const { animeLists } = props
    // const [ isFetching, setIsFetching]  = useState(false);
    // const [ hasMore, setHasMore ] = useState(true)
    const year = useRecoilValue(Year)
    const { select_cour } = useCourFactory()
    const hasMore = useRecoilValue(HasMore)
    // const [ apiKeyValue, setApiKeyValue ] = useRecoilState(ApiKeyValue)
    // const [ animeLists, setAnimeLists ] = useRecoilState(AnimeLists)
    const [ loadPage, setLoadPage ] = useRecoilState(LoadPage)
    const { getNextAnimes, animeLists, isLoading } = useNextAnimes(loadPage)

    // const loadMore = (page: number) => {
    //     console.log(page);
    //     console.log(animeLists.length);
    //     // animeLists.length < 1 && 
    //     getNextAnimes(page)
    //     // try {
    //     //     setIsFetching(true);
    //     //     const filter_season = `${apiKeyValue.year}-${apiKeyValue.cour}`
    //     //     const getanime = await client.get('/api/annicts/search', {
    //     //         params: {
    //     //             filter_season,
    //     //             page
    //     //         }
    //     //     })
    //     //     const animeDate: AnnictApiType[] = getanime.data.works
    //     //     console.log(getanime);

    //     //     if (animeDate.length < 1) {
    //     //         setHasMore(false);
    //     //         return;
    //     //     }
    //     //     const distinctList = new Set([...animeLists, ...animeDate])
    //     //     // const rep = Array.from(s)
    //     //     const list: AnnictApiType[] = [...distinctList]

    //     //     // console.log(rep);

    //     //     setAnimeLists([...list])
    //     //     console.log(animeLists);
    //     //     console.log(getanime);
    //     // } catch (error) {
    //     //     showMessage({title: 'アニメ一覧の取得に失敗しました', status: "error"})
    //     // } finally {
    //     //     setIsFetching(false);
    //     // }

    // }


    // const [ page, setPage ] = useState(1)

    // ref対象を監視して表示終わったら、ページ番号を増やす
    const observer: any = useRef()
    const lastMovieElementRef = useCallback((node) => {
        if (isLoading) return
        observer.current && observer.current.disconnect()

        observer.current = new IntersectionObserver((entries) => {
            //要素が交差しているかつさらにページを読みこむ場合、ページ番号を増やす
            if(entries[0].isIntersecting && hasMore){
                setLoadPage((prevPage) => prevPage + 1)
            }
        })

        node && observer.current.observe(node)

    }, [hasMore, isLoading])

    return (
        <>
            <Box justify='center' align='center' m='30px 10px 0'>
                <Text fontWeight='700' fontSize='1.17em'>
                    ― {year}{select_cour} ―
                </Text>
            </Box>
            {/* simpleGridに変更⇩ */}
            {animeLists ? 
                <Wrap p={{base: 3, md: 4}} w='100%' spacing={5} justify='center' align='center'>
                    {/* <InfiniteScroll loadMore={loadMore} hasMore={!isFetching && hasMore} pageStart={0} loader={loder}> */}
                        {console.log(animeLists)}
                        {animeLists.map((animeList, index) => (
                            animeLists.length === index + 1 ? (
                                <WrapItem key={index} ref={lastMovieElementRef}>
                                    <AnimeCard animeList={animeList}/>
                                </WrapItem>
                            ) : (
                                <WrapItem key={index}>
                                    <AnimeCard animeList={animeList}/>
                                </WrapItem>
                            )
                            ))}
                        {isLoading && 
                                <Flex>
                                    <Center p={8} w='100%' h='100'>
                                        <BeatLoader size={10} color="black" />
                                    </Center>

                                </Flex>
                        }
                    {/* </InfiniteScroll> */}
                </Wrap>
                : null }
        </>
    )
})

export default AnimeCardList
