import { Box, Container, Image, Text } from "@chakra-ui/react";
import { VFC } from "react";
import { useLocation } from "react-router-dom";
import { AnnictApiType } from "types/api/AnnictApiType";

type LocationState = {
    hash: ""
    key: string;
    pathname: string;
    search: string;
    state: string | undefined;
}

const ReviewPage: VFC = () => {
    const location = useLocation<Location<LocationState>()
    const images = location.state
    // // const { images } = state
    console.log(images);

    const image = images ? images : 'https://lab.esprlog.net/annict/img/no_image.png'

    return (
        <Container maxW={{base: 'container.sm', md: 'container.lg'}} h='100vh' bg='#ffff' m='100px auto 20px auto' borderRadius='10px'>
            <Box >
                <Image src={image}  w='calc(100% + 20px)' objectFit='cover' maxH={{base: '200px', md: '260px'}}/>
            </Box> 
        </Container>
    )
}

export default ReviewPage
