import { Box, Button, Center, Divider, Flex, FormControl, FormErrorMessage, FormLabel, Heading, Input, VStack } from "@chakra-ui/react"
import { useForm } from "react-hook-form"
import { BeatLoader } from "react-spinners"
import { EditPasswordUpdate } from "types/form/FormInputs"

const EditPassword = () => {
    const { register, handleSubmit, formState: {errors}, formState } = useForm<EditPasswordUpdate>({
        mode: 'all',
        defaultValues: {
            password: '',
            passwordConfirmation: ''
        }
    })

    const handleUpdatePassword = () => {

    }
    return (
        <>
            <Flex justify='center' align='center' h='100%' mt='90px'>
                <Box bg='white' w='2xl' p={4} borderRadius='10px' shadow='md'>
                    <Heading as='h1' size='lg' textAlign='center'>パスワード変更</Heading>
                    <Divider my={4}/>
                    <form onSubmit={handleSubmit(handleUpdatePassword)}>
                        <VStack spacing={5}>
                            <FormControl id='password' isRequired isInvalid={errors.password ? true : false}>
                                <FormLabel>ユーザーネーム</FormLabel>
                                <Input 
                                    type='password'
                                    {...register('password', {required: true, maxLength: 10})}
                                />
                                <FormErrorMessage>
                                    {errors.password?.type === 'required' && 'ユーザーネームは必須です。' }
                                    {errors.password?.type === 'maxLength' && 'ユーザーネームは10文字以内で設定してください。' }
                                </FormErrorMessage>
                            </FormControl>
                            <FormControl id='passwordConfirmation' isRequired isInvalid={errors.password ? true : false}>
                                <FormLabel>メールアドレス</FormLabel>
                                <Input 
                                   type='password'
                                    // name='email' 
                                    {...register('passwordConfirmation', { required: 'パスワードは必須です', pattern: {value: /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/, message: "メールアドレス形式で入力してください。"}, })}
                                />
                                <FormErrorMessage>
                                    {errors.passwordConfirmation?.type === 'required' && '確認用パスワードは必須です。'}
                                    {errors.passwordConfirmation?.type === 'validate' && "確認用パスワードが一致しません。"}
                                </FormErrorMessage>
                            </FormControl>
                            <Button type="submit" disabled={!formState.isValid} isLoading={formState.isSubmitting} w='100%' color='white' bg='blue.400' _hover={{opacity: 0.8}} spinner={<BeatLoader size={8} color="white" />}>更新</Button>
                        </VStack>
                    </form>
                </Box>
            </Flex>

        </>
    )
}

export default EditPassword
