import { ChangeEvent, ChangeEventHandler } from "react";
import { useRecoilState } from "recoil";
import { Year } from '../store/Year'

type handleChangeYear = (e: ChangeEvent<HTMLSelectElement>) => void
export const useYearFactory = () => {
    const current_year = useCurrentYearFactory();
    const year: number[] = []
    const [ selectYear, setSelectyear ] = useRecoilState(Year)

    const handleChangeYear: handleChangeYear = (e) => {
        console.log(e);

        setSelectyear(e.target.value)
    }

    for (let y = current_year; y >= 2014; y--) {
        year.push(y)
    }
}
