import { Image } from '@chakra-ui/image'
import { Box, Flex, Link, Text } from '@chakra-ui/layout'
import React, { memo, VFC, useState, useEffect } from 'react'
import { useHistory } from 'react-router'
import { BsHeartFill, BsHeart } from "react-icons/bs";
import { motion, useAnimation } from 'framer-motion';
import { Icon } from '@chakra-ui/react';

import { AnnictApiType } from '../../../types/api/AnnictApiType';
import { client } from 'lib/api/client';
import { useRecoilValue } from 'recoil';
import { CurrentUser } from 'store/auth/Auth';

type Props = {
    animeList: AnnictApiType;
}

type favorites = {
    user_id: number;
    title: string;
    official_url: string;
    image_url: string;
    twitter_username: string;
    season: string;
    api_id: number;
}

const AnimeCard: VFC<Props> = memo((props) => {
    const { animeList } = props
    const [ isLike, setIsLike ] = useState(false)
    const currentUser = useRecoilValue(CurrentUser)
    const controls = useAnimation()

    const image = animeList.images.recommended_url !== '' ? animeList.images.recommended_url : 'https://lab.esprlog.net/annict/img/no_image.png'

    const toggleLike = () => {
        setIsLike(!isLike)
        console.log(animeList);

        if(!isLike) {
            controls.start({ scale: [0, 0.5, 1] })
        }else {
            controls.start({ translateX: [0, 0, 0, -7, 0, -7, 0, 0] })
        }
    }

    useEffect(() => {
        const setToggle = async() => {
            const favoriteParams: favorites = {
                user_id: animeList.id,
                title: animeList.title,
                official_url: animeList.official_site_url,
                image_url: animeList.images.recommended_url,
                twitter_username: animeList.twitter_username,
                season: animeList.season_name_text,
                api_id: animeList.id,
            }

            client.post('/api//annicts/favorites/create', {
                params: {
                    favorite: favoriteParams,
                }
            })
        }
        setToggle()
        // return () => {
        //     cleanup
        // }
    }, [isLike])

    return (
        <>
            <Box w={{base: '365px', md: '500px'}} h={{base: '280px', md: '330px'}} bg='white' borderRadius='10px' m='15px 5px' p='0 0 10px 0' border='solid 1px #e0e0e0' position='relative'>
                <Link as='a' href={animeList.official_site_url} target='_blank'>
                    <Box display='block' m='0 0 8px' overflow='hidden' borderRadius='10px 10px 0 0'>
                        <motion.div  whileHover={{opacity: 1, scale: 1.1, transition: { duration: 0.3 }}} >
                            <Image src={image}  w='calc(100% + 20px)' objectFit='cover' maxH={{base: '200px', md: '260px'}} _hover={{filter: 'blur(3px)'}} />
                        </motion.div>
                        <Box>
                            <Text opacity='0'color='red' fontWeight='700' fontSize='25px' position='absolute' top='40%' right='30%'>View Official Site</Text>
                        </Box>
                    </Box>
                </Link>
                <Flex align='flex-start' m='10px 15px 0'>
                    <Text as='h1' fontSize="md" fontWeight='800'>{animeList.title}</Text>
                </Flex> 
                <Flex justify='flex-end' w='100%'>
                    <Box position='absolute' bottom='5px' right='5px'>
                        <motion.div
                            onClick={toggleLike}
                            animate={controls}
                            transition={!isLike ? { duration: 0.2 , ease: "easeIn"} : { duration: 0.6 , ease: "easeIn"}}
                        >
                            <Icon
                                as={isLike ? BsHeartFill : BsHeart }
                                fontSize='20px' 
                                color={isLike ? '#FF1493': ''}
                                cursor='pointer'
                            />
                        </motion.div>
                    </Box>
                </Flex>
            </Box>
        </>
    )
})

export default AnimeCard
