import { useState } from "react";
    type Size = () => {
        size: string;
    }
    export const useGetSize: Size  = () => {
        const [ size, setSize ] = useState('');
        //画面幅に応じたモーダルサイズ設定
        const innerWidth = window.innerWidth
        innerWidth < 768 ? setSize('sm') : setSize('md')

        return { size }
    }
