import { Avatar, Box, Divider, Flex, Text, VStack } from "@chakra-ui/react";
import { memo, VFC } from "react";
import ReactStars from "react-stars";

type Props = {
    review: {
        name: string;
        title: string;
        body: string;
    }
}

const ReviewList: VFC<Props> = memo((props) => {
    const { name, title, body } = props.review
    return (
        <Box>
            <Divider size={'2xl'} m='10px 0 10px 0'/>
            <Box mt={5}>
                <VStack spacing={2} align='flex-start'>
                    <Flex align='center'>
                        <Avatar size='sm' mr={2}/>
                        <Text as='p'>{name}</Text>
                    </Flex>
                    <Flex align='center'>
                        <Text as='p' mr={2} fontWeight='bold'>{title}</Text>
                        <ReactStars edit={false}/>
                    </Flex>
                    <Text>{body}</Text>
                </VStack>
            </Box>
    </Box>
    )
})

export default ReviewList
