import { useEffect } from 'react'
import { Redirect, Route, Switch, useHistory } from "react-router-dom"
import TopPage from "../components/pages/TopPage"
import SignUpForm from '../components/pages/auth/SigninForm'
import SigninForm from '../components/pages/auth/SigninForm'
import { AuthLoding } from "store/loding/AuthLoding"
import { CurrentUser, IsSignedIn } from "store/auth/Auth"
import { useRecoilState, useRecoilValue } from "recoil"
import { getCurrentUser } from "lib/api/auth/auth"

const Router = () => {
    const [ authLoding, setAuthLoding] = useRecoilState(AuthLoding)
    const [ isSignedIn, setIsSignedIn ] = useRecoilState(IsSignedIn)
    const [ currentUser, setCurrentUser ] = useRecoilState(CurrentUser)

    const history = useHistory()

    // 認証済みのユーザーがいるかどうかチェック
    // 確認できた場合はそのユーザーの情報を取得
    const handleGetCurrentUser = async () => {
        try {
        const res = await getCurrentUser()

        if (res?.data.isLogin === true) {
            setIsSignedIn(true)
            setCurrentUser(res?.data.data)

            console.log(res?.data.data)
        } else {
            console.log("No current user")
        }
        } catch (err) {
        console.log(err)
        }

        setAuthLoding(false)
    }

    useEffect(() => {
        handleGetCurrentUser()
    }, [setCurrentUser])

    // ユーザーが認証済みかどうかでルーティングを決定
    // 未認証だった場合は「/signin」ページに促す
    const Private = ({ children }: { children: React.ReactElement }) => {
        if (!authLoding) {
            if (isSignedIn) {
                console.log(isSignedIn);
                return children
            } else {
                console.log(isSignedIn);

                return <Redirect to="/signin" />
            }
        } else {
            console.log(10);
            return <></>
        }
    }

    return (
        <Switch>
            <Private>
                <Route exact path='/' render={() => (
                    <TopPage />
                )} />

            {/* <Route path='' render={() => (
                <LikeAnime />
            )} /> */}
            {/* <Route path='' render={() => (
                <Login />
            )} /> */}
            {/* <Route path='' render={() => (
                <MyAccount />
            )} /> */}
            </Private>

            <Route exact path="/signup" render={() => (
                <SignUpForm />
            )} />
            <Route exact path="/signin" render={() => (
                <SigninForm />
            )} />
            <Route />
        </Switch>
    )
}

export default Router
