import { Route, Switch, useHistory } from "react-router-dom"
import TopPage from "../components/pages/TopPage"
import SignUpForm from '../components/pages/auth/SigninForm'
import SigninForm from '../components/pages/auth/SigninForm'
import { AuthLoding } from "store/loding/AuthLoding"
import { IsSignedIn } from "store/auth/Auth"
import { useRecoilValue } from "recoil"

const Router = () => {
    const authLoding = useRecoilValue(AuthLoding)
    const isSignedIn = useRecoilValue(IsSignedIn)

    const history = useHistory()
    // ユーザーが認証済みかどうかでルーティングを決定
    // 未認証だった場合は「/signin」ページに促す
    const Private = ({ children }: { children: React.ReactElement }) => {
        if (!authLoding) {
        if (IsSignedIn) {
            return children
        } else {
            history.push({path: '/signin'})
        }
        } else {
        return <></>
        }
    }
    return (
        <Switch>
            <Route exact path='/' render={() => (
                <TopPage />
            )} />
            <Route exact path="/signup" render={() => (
                <SignUpForm />
            )} />
            <Route exact path="/signin" render={() => (
                <SigninForm />
            )} />
            {/* <Route path='' render={() => (
                <LikeAnime />
            )} /> */}
            {/* <Route path='' render={() => (
                <Login />
            )} /> */}
            {/* <Route path='' render={() => (
                <MyAccount />
            )} /> */}
            <Route />
        </Switch>
    )
}

export default Router
