import React, { memo } from "react"
import { Drawer, DrawerContent, DrawerOverlay, DrawerHeader, DrawerBody, useDisclosure, Box, Text, Divider, DrawerCloseButton, Flex, IconButton } from "@chakra-ui/react"
import { RiMenuFoldLine } from "react-icons/ri";
import { HamburgerIcon } from '@chakra-ui/icons'
import { Link } from "react-router-dom"
import { useRecoilValue } from "recoil";
import { CurrentUser } from "store/auth/Auth";
import { User } from "types/auth/authTypes";

const MenuBar = memo(() => {
    const { isOpen, onOpen, onClose } = useDisclosure()
    const firstField = React.useRef()
    const current_user = useRecoilValue(CurrentUser)
    const id = current_user ? current_user.id : ''
    console.log(current_user);
    return (
        <>
            <Flex w='70px' h='70px' cursor='pointer' align='center' justify='center'>
             <IconButton
                w='40px' 
                h='40px'
                aria-label="メニュー"
                icon={<HamburgerIcon boxSize={8}/>}
                size="xl"
                variant="unstyled"
                display='block'
                onClick={onOpen}
                initialFocusRef={firstField}
             />
            </Flex>
            <Drawer placement='right' size='xs' onClose={onClose} isOpen={isOpen} motionPreset='none'>
                <DrawerOverlay />
                <DrawerContent>
                    <DrawerCloseButton />
                    <DrawerHeader borderBottomWidth="1px">MENU</DrawerHeader>
                    <DrawerBody p='0'>
                        <Box w="100%" p='16px 24px' _hover={{opacity: 0.8, backgroundColor: '#d7d7d8'}}>
                            <Link to='/' onClose={onClose}>プロフィール</Link>
                        </Box>
                        <Divider />
                        <Box w="100%" p='16px 24px' _hover={{opacity: 0.8, backgroundColor: '#d7d7d8'}}>
                            <Link to={`like/${id}`} onClick={onClose}>お気に入りアニメ</Link>
                        </Box>
                    </DrawerBody>
                </DrawerContent>
            </Drawer>
        </>
    )
})

export default MenuBar
