import { Box, Button, Center, Image, Text, VStack } from "@chakra-ui/react";
import ReviewList from "components/organisms/ReviewList";
import { useGetAnimeReviews } from "customHooks/useGetAnimeReviews";
import { memo, useState, VFC, useEffect, useCallback } from "react";
import { useLocation, useParams } from "react-router-dom";

const ReviewPage: VFC = memo(() => {
    const [ loadIndex, setLoadIndex ] = useState(4)
    const [ isEmpty, setIsEmpty ] = useState(false);
    const location = useLocation<string>()
    const { anime_id } = useParams<{anime_id: string}>()
    const { reviewsList } = useGetAnimeReviews(anime_id)
    const images = location.state


    const image = images ? images : 'https://lab.esprlog.net/annict/img/no_image.png'
    console.log(reviewsList.length);
    console.log(loadIndex);

    const replaceList = useCallback(
        (): number  => {
            const list = [...reviewsList]
        return list.length
        },[reviewsList])

    //「さらに表示」ボタンの制御
    const displayMore = useCallback(() => {
        const listCount = replaceList()
        if(loadIndex < listCount) {
            setLoadIndex((prevState) => prevState + 4);
        }
    },[])

    useEffect(() => {
        let isMounted = true
        const listCount = replaceList()
        const checkLodaList = () => {
            loadIndex > listCount && setIsEmpty(true)
        }
        isMounted && checkLodaList()
        return () => { isMounted = false }
    }, [loadIndex])

    return (
        <Box w={{base: '80%', md: '60%'}} h='100%' bg='#ffff' m='100px auto 20px auto' borderRadius='10px'>
            <Box w='100%'>
                <Image src={image}  borderRadius='10px 10px 0 0' w='100%' objectFit='cover'/>
            </Box>

            <Box p={3}>
                <Text as='h2' fontWeight='bold'>レビュー一覧</Text>
                <VStack spacing={5} align='start'>
                    {reviewsList ? (
                        reviewsList.slice(0, loadIndex).map((review, key) => (
                            <ReviewList key={key} review={review}/>
                        ))
                        ) : null }
                </VStack>
                <Center mt={10}>
                    <Button
                    colorScheme='teal'
                    disabled={isEmpty}
                    onClick={displayMore}
                    variant="solid"
                    >
                        さらに表示
                    </Button>
                </Center>
            </Box>
        </Box>
    )
})

export default ReviewPage
