import { useState } from "react";
    type Size = () => {
        partsSize: string;
    }
    export const useGetSize: Size  = () => {
        const [ partsSize, setPartsSize ] = useState('');
        //画面幅に応じたモーダルサイズ設定
        const innerWidth = window.innerWidth
        innerWidth < 768 ? setPartsSize('sm') : setPartsSize('md')

        return { partsSize }
    }
