import { Box, Button, Divider, Flex, FormControl, FormLabel, Heading, Input, Text } from "@chakra-ui/react"
import Header from "components/organisms/Header"
import { useForm } from "react-hook-form"

type FormInputs = {
    email: string;
    password: string;
  };

const SigninForm = () => {
    const { register, formState: { errors }, handleSubmit, formState } = useForm<FormInputs>({
        mode: 'all',
        defaultValues: {
            email: '',
            password: '',
          }
    })
    return (
        <>
            {/* <Header /> */}
            <Box w={{base: '98%', md: '70%'}} h='100%' m='100px auto 20px auto'>
                <Flex justify='center' align='center' h='100vh'>
                    <Box bg='white' w='sm'>
                        <Heading as='h1'>ログイン</Heading>
                        <Divider />
                        <form>
                            <FormControl>
                                <FormLabel id="email">メールアドレス</FormLabel>
                                <Input type="email" name='email' placeholder="test@example.com"/>
                            </FormControl>
                            <FormControl>
                                <FormLabel id="email">メールアドレス</FormLabel>
                                <Input type="email" name='email' placeholder="test@example.com"/>
                            </FormControl>
                            <Button>ログイン</Button>
                        </form>

                    </Box>
                </Flex>  
            </Box>

        </>
    )
}

export default SigninForm
