import { memo, VFC } from "react";

import { Avatar, Box, Divider, Flex, Text, VStack } from "@chakra-ui/react";
import ReactStars from "react-stars";

import { AnimeReviewType } from "types/review/AnimeReviewType";

type Props = {
    review: AnimeReviewType;
}

const ReviewList: VFC<Props> = memo((props) => {
    const { name, title, comment, image, rate, created_at } = props.review

    console.log();

    return (
        <Box w='100%'>
            <Divider src={image} pt='20px'/>
            <Box mt={5}>
                <VStack spacing={2} align='flex-start'>
                    <Text as='p' fontSize='15px'>{`投稿日:${created_at}`}</Text>
                    <Flex align='center'>
                        <Avatar size='sm' mr={2}/>
                        <Text as='p'>{name}</Text>
                    </Flex>
                    <Flex align='center'>
                        <Text as='p' mr={2} fontWeight='bold'>{title}</Text>
                        <ReactStars edit={false} value={rate}/>
                    </Flex>
                    <Text>{comment}</Text>
                </VStack>
            </Box>
    </Box>
    )
})

export default ReviewList
