import { Avatar, Box, Container, Divider, Flex, Image, Text, VStack } from "@chakra-ui/react";
import { VFC } from "react";
import { useLocation } from "react-router-dom";
import ReactStars from "react-stars";
import { AnnictApiType } from "types/api/AnnictApiType";

const ReviewPage: VFC = () => {
    const location = useLocation<string>()
    const images = location.state
    console.log(images);

    const image = images ? images : 'https://lab.esprlog.net/annict/img/no_image.png'

    return (
        <Box w={{base: '80%', md: '60%'}} h='100%' bg='#ffff' m='100px auto 20px auto' borderRadius='10px'>
            <Box w='100%'>
                <Image src={image}  borderRadius='10px 10px 0 0' w='100%' objectFit='cover'/>
            </Box>

            <Box p={3}>
                <Text as='h2' mb={2} fontWeight='bold'>レビュー一覧</Text>
                {/* <Divider size={'2xl'}/> */}
                <VStack spacing={5} align='start'>
                    <Box mt={2}>
                        <Divider size={'2xl'} m='10px 0 10px 0'/>
                        <Box mt={10}>
                            <VStack spacing={3} align='flex-start'>
                                <Flex align='center'>
                                    <Avatar size='sm' mr={2}/>
                                    <Text as='p'>ネーム</Text>
                                </Flex>
                                <Flex align='center'>
                                    <Text as='p' mr={2} fontWeight='bold'>タイトル</Text>
                                    <ReactStars edit={false}/>
                                </Flex>
                                <Text>テストテストテストテストテストテストテストテストテストテストテストテストテストテストテストテストテストテストテストテストテスト</Text>
                            </VStack>
                        </Box>
                    </Box>
                    {/* <Divider size={'2xl'}/> */}
                </VStack>
            </Box>
        </Box>
    )
}

export default ReviewPage
