import { EditIcon } from "@chakra-ui/icons";
import { Button, FormControl, FormLabel, Input, Modal, ModalBody, ModalCloseButton, ModalContent, ModalFooter, ModalHeader, ModalOverlay, Textarea, useDisclosure } from "@chakra-ui/react";
import { memo, useRef, VFC } from "react";
import { MdBluetooth } from "react-icons/md";

type  Props = {
    id: number
}
const ReviewModal: VFC<Props> = memo((props) => {
    const { id } = props
    const { isOpen, onOpen, onClose } = useDisclosure()
    const initialRef = useRef<HTMLInputElement>(null)

    return (
        <>
            <Button pl={0} size='xs' leftIcon={<EditIcon />} variant='none' onClick={onOpen}>レビューを書く</Button>

            <Modal
                initialFocusRef={initialRef}
                isOpen={isOpen}
                onClose={onClose}
            >
                <ModalOverlay />
                <ModalContent>
                <ModalHeader>アニメレビュー</ModalHeader>
                <ModalCloseButton />
                <ModalBody pb={6}>
                    <FormControl>
                    <FormLabel>レビュー評価</FormLabel>
                        <Input ref={initialRef} placeholder='First name' />
                    </FormControl>

                    <FormControl mt={4}>
                    <FormLabel>レビューコメント</FormLabel>
                    <Textarea />
                    </FormControl>
                </ModalBody>

                <ModalFooter>
                    <Button colorScheme='blue' mr={3}>
                    Save
                    </Button>
                    <Button onClick={onClose}>Cancel</Button>
                </ModalFooter>
                </ModalContent>
            </Modal>
        </>
    )
})

export default ReviewModal
