import { useEffect, useState } from "react";

type Size = () => {
    partsSize: string
}

export const useGetSize: Size = () => {
    const [ partsSize, setPartsSize ] = useState('sm');
    // //画面幅に応じたモーダルサイズ設定
    const innerWidth = window.innerWidth > 768
    useEffect(() => {
        innerWidth && setPartsSize('md')
    }, [partsSize])

    return { partsSize }
}
