import { useEffect, useState } from "react";

type Size = (val: string) => {
    partsSize: string
}

export const useGetSize: Size = (cal) => {
    const [ partsSize, setPartsSize ] = useState('xs');
    // //画面幅に応じたモーダルサイズ設定
    const innerWidth = window.innerWidth > 768
    useEffect(() => {
        switch (cal) {
            case 'modal':

                break;
            case 'drawer':

                break;

            default:
                break;
        }
        innerWidth && setPartsSize('md')
    }, [innerWidth])

    return { partsSize }
}
