import { Image } from '@chakra-ui/image'
import { Badge, Box, Divider, Flex, HStack, Link, Stack, Text } from '@chakra-ui/layout'
import React, { memo, useCallback, VFC, useState } from 'react'
import { useHistory } from 'react-router'
import { BsHeartFill, BsHeart } from "react-icons/bs";
import { motion, useAnimation } from 'framer-motion';
import { Icon } from '@chakra-ui/react';
import { AnnictApiType } from '../../../types/api/AnnictApiType';

type Props = {
    animeList: AnnictApiType;
}

const AnimeCard: VFC<Props> = (props) => {
    const { animeList } = props
    const [ isLike, setIsLike ] = useState(false)
    const controls = useAnimation()

    const image = animeList.images.recommended_url !== '' ? animeList.images.recommended_url : 'https://lab.esprlog.net/annict/img/no_image.png'

    const toggleLike = () => {
        setIsLike(!isLike)

        if(!isLike) {
            controls.start({ scale: [0, 0.5, 1] })
        }else {
            controls.start({ translateX: [0, 0, 0, -7, 0, -7, 0, 0] })
        }
    }

    return (
        <>
            <Link _hover={{opacity: 0.8}} >
                <Box w={{base: '300px', md: '500px'}} h={{base: '250px', md: '350px'}} bg='white' borderRadius='10px' m='15px 5px' p='0 0 10px 0' border='solid 1px #e0e0e0'　>
                    <Image src={image} borderRadius='10px 10px 0 0' w='100%'　h='80%' objectFit='cover'/>
                    <Flex align='flex-start' m='5px 15px 0'>
                        <Text fontSize="lg">{animeList.title}</Text>
                    </Flex>
                    <Flex justify='flex-end' mr={4} mb={3} >
                        <Box>
                            <motion.div
                                onClick={toggleLike}
                                animate={controls}
                                transition={!isLike ? { duration: 0.2 , ease: "easeIn"} : { duration: 0.6 , ease: "easeIn"}}
                            >
                                <Icon
                                    as={isLike ? BsHeartFill : BsHeart }
                                    fontSize='20px' 
                                    color={isLike ? '#FF1493': ''}
                                />
                            </motion.div>
                        </Box>
                    </Flex>
                </Box>
            </Link>
        </>
    )
}

export default AnimeCard
