import { FormLabel, Select } from "@chakra-ui/react"
import { ChangeEvent, ChangeEventHandler } from "react";
import { useRecoilState } from "recoil";
import { useCurrentYearFactory } from "../../../customHooks/useYearFactory";
import { Year } from '../../../store/Year'

const YearInput = () => {
    const current_year = useCurrentYearFactory();
    const year: number[] = []
    const [ selectYear, setSelectyear ] = useRecoilState(Year)

    const handleChangeYear: handleChangeYear = (e) => {
        console.log(e);

        setSelectyear(e.target.value)
    }

    for (let y = current_year; y >= 2014; y--) {
        year.push(y)
    }
    console.log(selectYear);

    return (
        <>
            <FormLabel>西暦</FormLabel>
            <Select onChange={handleChangeYear}>
                {year.map((year, index) => (
                    <option key={year}>{year}</option>
                ))}
            </Select>
        </>
    )
}

export default YearInput
