import { useEffect, useState } from 'react'

import Cookies from "js-cookie"
import { client } from "lib/api/client"
import { getReviewCount } from 'lib/api/review/review'

export const useGetReviewCount = (id: number) => {
    const [ count, setCount ] = useState(0)
    const [ rate, setRate] = useState(0)

    const getReviewStatus = async() => {
        try {
            const reviewCount = await getReviewCount(id)
            const { review_const, average_count } = reviewCount.data
            setCount()

        } catch (error) {
            console.error(error)
        }
    }

    useEffect(() => {
        let isMounted = true
        isMounted && getReviewStatus()
        return () => {
            isMounted = false
        }
    }, [])

    return { count, rate }
}
