import { useState, useCallback, useEffect } from "react";

import { useRecoilState } from "recoil";

import { client } from "lib/api/client";
import { AnimeLists } from "store/AnimeLists";
import { ApiKeyValue } from "store/ApiKeyValue";
import { HasMore } from "store/loding/HasMore";
import { AnnictApiType } from "types/api/AnnictApiType";
import { useMessage } from "./message/useMessage";
import { LoadPage } from "store/LoadPage";


type NextAnimes = (page: number) => {
    getNextAnimes: () => void;
    // isFetching: boolean;
    isLoading: boolean;
    animeLists: AnnictApiType[];
}

export const useNextAnimes: NextAnimes = (page: number) => {
    const [ isFetching, setIsFetching ]  = useState(false);
    const [ isLoading, setIsLoding ]  = useState(false);
    const [ hasMore, setHasMore ] = useRecoilState(HasMore)
    const [ apiKeyValue, setApiKeyValue ] = useRecoilState(ApiKeyValue)
    const [ animeLists, setAnimeLists ] = useRecoilState(AnimeLists)
    const [ loadPage, setLoadPage ] = useRecoilState(LoadPage)
    const { showMessage } = useMessage()

    // const getNextAnimes = useCallback(async(page: number) => {
    //     try {
    //         setIsFetching(true);
    //         const filter_season = `${apiKeyValue.year}-${apiKeyValue.cour}`
    //         const getanime = await client.get('/api/annicts/search', {
    //             params: {
    //                 filter_season,
    //                 page
    //             }
    //         })
    //         const animeDate: AnnictApiType[] = getanime.data.works
    //         console.log(getanime);

    //         if (animeDate.length < 1) {
    //             setHasMore(false);
    //             return;
    //         }
    //         const distinctList = new Set([...animeLists, ...animeDate])
    //         // const list: AnnictApiType[] = [...distinctList]

    //         setAnimeLists([...distinctList])
    //         console.log(animeLists);
    //         console.log(getanime);
    //     } catch (error) {
    //         showMessage({title: 'アニメ一覧の取得に失敗しました', status: "error"})
    //     } finally {
    //         setIsFetching(false);
    //     }    
    // },[animeLists])
    const getNextAnimes = useCallback(async() => {        
        try {
            setIsFetching(true);
            setIsLoding(true)
            const filter_season = `${apiKeyValue.year}-${apiKeyValue.cour}`
            const getanime = await client.get('/api/annicts/search', {
                params: {
                    filter_season,
                    page
                }
            })
            const animeDate: AnnictApiType[] = getanime.data.works
            if (animeDate.length < 1) {
                setHasMore(false);
            }

            //重複データ削除
            const dataList = [...animeLists, ...animeDate]
            console.log(dataList);

            // const distinctDataList = dataList.filter((data, index, self) => {
            //     const ids = self.map(data => data.id)

            //     if(ids.indexOf(data.id) === index){
            //         return data;
            //     }
            // })

            let map = new Map(dataList.map<[number, AnnictApiType]>(data => [data.id, data]));
            // const distinctDataList = [...map.values()]
            const distinctDataList = Array.from(map.values())

            setAnimeLists(distinctDataList)
            setIsLoding(false)
        } catch (error) {
            showMessage({title: 'アニメ一覧の取得に失敗しました', status: "error"})
        } finally {
            setIsFetching(false);
        }    
    },[loadPage])

    useEffect(() => {
        let isMounted = true
        isMounted && getNextAnimes()
        return () => {
            isMounted = false
        }
    }, [loadPage])

    return { getNextAnimes, animeLists, isLoading }
}
