import { Box, Button, Divider, Flex, FormControl, FormErrorMessage, FormLabel, Heading, Input, Text, VStack } from "@chakra-ui/react"
import { useForm } from "react-hook-form"
import { BeatLoader } from "react-spinners"

type FormInputs = {
    email: string;
    password: string;
  };

const SigninForm = () => {
    const { register, formState: { errors }, handleSubmit, formState } = useForm<FormInputs>({
        mode: 'all',
        // defaultValues: {
        //     email: '',
        //     password: '',
        //   }
    })
    return (
        <>
            {/* <Header /> */}
            {/* <Box> */}
                <Flex justify='center' align='center' h='100vh'>
                    <Box bg='white' w='sm' p={4} borderRadius='10px' shadow='md'>
                        <Heading as='h1' size='lg' textAlign='center'>ログイン</Heading>
                        <Divider my={4}/>
                        <form>
                            <VStack spacing={5}>
                                <FormControl id="email" isRequired isInvalid={errors.email ? true : false}>
                                    <FormLabel>メールアドレス</FormLabel>
                                    <Input 
                                        // type='email'
                                        // name='email' 
                                        placeholder="test@example.com" 
                                        {...register('email', { required: "メールアドレスは必須です。", pattern: {value: /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/, } })}
                                    />
                                    <FormErrorMessage>
                                        {errors.email && errors.email.message}
                                    </FormErrorMessage>
                                </FormControl>
                                <FormControl id="password">
                                    <FormLabel>パスワード</FormLabel>
                                    <Input type="text" name='password'/>
                                </FormControl>
                                <Button w='100%' color='white' bg='blue.400' _hover={{opacity: 0.8}} isLoading={false} spinner={<BeatLoader size={8} color="white" />}>ログイン</Button>
                            </VStack>
                        </form>

                    </Box>
                </Flex>  
            {/* </Box> */}

        </>
    )
}

export default SigninForm
