import { Box, Container, Image, Text } from "@chakra-ui/react";
import { VFC } from "react";
import { useLocation } from "react-router-dom";
import { AnnictApiType } from "types/api/AnnictApiType";

const ReviewPage: VFC = () => {
    const location = useLocation<string>()
    const images = location.state
    // // const { images } = state
    console.log(images);

    const image = images ? images : 'https://lab.esprlog.net/annict/img/no_image.png'

    return (
        <Box w={{base: '90%', md: '70%'}} h='100vh' bg='#ffff' m='100px auto 20px auto' borderRadius='10px'>
            <Box w='100%'>
                <Image src={image}  w='100%' objectFit='cover' maxH={{base: '200px', md: '260px'}}/>
            </Box> 
        </Box>
    )
}

export default ReviewPage
